package com.victor.data;import java.io.Serializable;/** * Created by victor on 2017/4/27. */public class Media implements Serializable{    private int id;    private String caption;    private String video;    private String created_at;    private int comments_count;    private int likes_count;    private int reposts_count;    private MeiPaiUser user;    public void setId(int id) {        this.id = id;    }    public void setCaption(String caption) {        this.caption = caption;    }    public void setVideo(String video) {        this.video = video;    }    public void setCreated_at(String created_at) {        this.created_at = created_at;    }    public void setComments_count(int comments_count) {        this.comments_count = comments_count;    }    public void setLikes_count(int likes_count) {        this.likes_count = likes_count;    }    public void setReposts_count(int reposts_count) {        this.reposts_count = reposts_count;    }    public void setUser(MeiPaiUser user) {        this.user = user;    }    public int getId() {        return id;    }    public String getCaption() {        return caption;    }    public String getVideo() {        return video;    }    public String getCreated_at() {        return created_at;    }    public int getComments_count() {        return comments_count;    }    public int getLikes_count() {        return likes_count;    }    public int getReposts_count() {        return reposts_count;    }    public MeiPaiUser getUser() {        return user;    }}