package com.victor.data;import java.io.Serializable;/** * Created by victor on 2017/4/27. */public class MeiPaiUser  implements Serializable {    private String screen_name;    private String avatar;    public void setScreen_name(String screen_name) {        this.screen_name = screen_name;    }    public void setAvatar(String avatar) {        this.avatar = avatar;    }    public String getScreen_name() {        return screen_name;    }    public String getAvatar() {        return avatar;    }}