package com.victor.model.data;import java.io.Serializable;import java.util.List;/** * Created by victor on 2016/10/19. */public class ProgramInfo implements Serializable{    private int action;    private String title;    private String img;    private String url;    private int gravity;    public void setAction(int action) {        this.action = action;    }    public void setTitle(String title) {        this.title = title;    }    public void setImg(String img) {        this.img = img;    }    public void setUrl(String url) {        this.url = url;    }    public void setGravity(int gravity) {        this.gravity = gravity;    }    public int getAction() {        return action;    }    public String getTitle() {        return title;    }    public String getImg() {        return img;    }    public String getUrl() {        return url;    }    public int getGravity() {        return gravity;    }}