package com.victor.model.impl;import android.text.TextUtils;import android.util.Log;import com.android.volley.Response;import com.android.volley.VolleyError;import com.victor.model.ProgramModel;import com.victor.model.data.ProgramData;import com.victor.module.VolleyRequest;import com.victor.presenter.OnProgramListener;import com.victor.util.Constant;/** * Created by victor on 2017/4/19. */public class ProgramModelImpl implements ProgramModel{    private String TAG = "ProgramModelImpl";    @Override    public void loadProgram(final OnProgramListener listener) {        String url = Constant.CATEGORY_URL_NEW;        VolleyRequest.newInstance().newFastJsonRequest(url, ProgramData.class, new Response.Listener<ProgramData>() {            @Override            public void onResponse(ProgramData programData) {                if (programData != null) {                    Log.e(TAG,"newFastJsonRequest-loadProgram-programData.getCount() = " + programData.getCount());                    listener.onSuccess(programData);                } else {                    listener.onError("no data response!");                }            }        }, new Response.ErrorListener() {            @Override            public void onErrorResponse(VolleyError error) {                String msg = error.getMessage();                if (TextUtils.isEmpty(msg)) {                    msg = "no data response!";                }                listener.onError(msg);            }        });    }}