package com.victor.presenter;import com.android.volley.Request;import com.victor.http.annotation.HttpParms;import com.victor.http.inject.HttpInject;import com.victor.http.module.VolleyRequest;import com.victor.http.presenter.impl.BasePresenterImpl;import com.victor.data.FunnyData;import com.victor.seagull.view.FunnyView;/** * Created by victor on 2017/4/19. */public class FunnyPresenterImpl <H,T> extends BasePresenterImpl<H,T> {    /*Presenter作为中间层，持有View和Model的引用*/    private FunnyView funnyView;    public FunnyPresenterImpl(FunnyView funnyView) {        this.funnyView = funnyView;    }    @Override    public void onSuccess(T data) {        if (funnyView == null) return;        if (data == null) {            funnyView.OnFunny(null,"no data response");        } else {            funnyView.OnFunny(data,"");        }    }    @Override    public void onError(String error) {        if (funnyView == null) return;        funnyView.OnFunny(null,error);    }    @Override    public void detachView() {        funnyView = null;    }    @HttpParms(method = Request.Method.GET,bodyContentType = VolleyRequest.mJsonBodyContentType,responseCls = FunnyData.class)    @Override    public void sendRequest(String url,H header,T parm) {        HttpInject.inject(this);        super.sendRequest(url,header,parm);    }}