package com.victor.presenter.impl;import com.android.volley.Request;import com.victor.http.annotation.HttpParms;import com.victor.http.inject.HttpInject;import com.victor.http.module.VolleyRequest;import com.victor.http.presenter.impl.BasePresenterImpl;import com.victor.model.data.ChannelData;import com.victor.seagull.view.ChannelView;/** * Created by victor on 2017/4/26. */public class ChannelPresenterImpl <H,T> extends BasePresenterImpl<H,T> {    /*Presenter作为中间层，持有View和Model的引用*/    private ChannelView channelView;    public ChannelPresenterImpl (ChannelView channelView) {        this.channelView = channelView;    }    @Override    public void onSuccess(T data) {        if (channelView == null) return;        if (data == null) {            channelView.OnChannel(null,"no data response");        } else {            channelView.OnChannel(data,"");        }    }    @Override    public void onError(String error) {        if (channelView == null) return;        channelView.OnChannel(null,error);    }    @Override    public void detachView() {        channelView = null;    }    @HttpParms(method = Request.Method.GET,bodyContentType = VolleyRequest.mJsonBodyContentType,responseCls = ChannelData.class)    @Override    public void sendRequest(String url,H header,T parm) {        HttpInject.inject(this);        super.sendRequest(url,header,parm);    }}