package com.victor.presenter.impl;import com.victor.model.ChannelModel;import com.victor.model.data.ChannelData;import com.victor.model.impl.ChannelModelImpl;import com.victor.presenter.ChannelPresenter;import com.victor.presenter.OnChannelListener;import com.victor.seagull.view.ChannelView;/** * Created by victor on 2017/4/26. */public class ChannelPresenterImpl implements ChannelPresenter,OnChannelListener{    /*Presenter作为中间层，持有View和Model的引用*/    private ChannelView channelView;    private ChannelModel channelModel;    public ChannelPresenterImpl (ChannelView channelView) {        this.channelView = channelView;        channelModel = new ChannelModelImpl();    }    @Override    public void getDatas() {        channelView.showLoading();        channelModel.loadChannels(this);    }    @Override    public void onSuccess(ChannelData channelData) {        channelView.hideLoading();        channelView.setDatas(channelData);    }    @Override    public void onError(String error) {        channelView.hideLoading();        channelView.showError(error);    }}