package com.victor.presenter.impl;import com.victor.model.FunnyModel;import com.victor.model.data.FunnyData;import com.victor.model.impl.FunnyModelImpl;import com.victor.presenter.FunnyPresenter;import com.victor.presenter.OnFunnyListener;import com.victor.seagull.view.FunnyView;/** * Created by victor on 2017/4/19. */public class FunnyPresenterImpl implements FunnyPresenter,OnFunnyListener{    /*Presenter作为中间层，持有View和Model的引用*/    private FunnyView funnyView;    private FunnyModel funnyModel;    public FunnyPresenterImpl(FunnyView funnyView) {        this.funnyView = funnyView;        funnyModel = new FunnyModelImpl();    }    @Override    public void getFunny(String url,int currentPage, String time) {        funnyView.showLoading();        funnyModel.loadFunny(url,currentPage, time, this);    }    @Override    public void onSuccess(FunnyData funnyData) {        funnyView.hideLoading();        funnyView.setFunnyInfo(funnyData);    }    @Override    public void onError(String error) {        funnyView.hideLoading();        funnyView.showError(error);    }}