package com.victor.presenter.impl;import com.victor.model.MeiPaiModel;import com.victor.model.data.MeiPaiData;import com.victor.model.impl.MeiPaiModelImpl;import com.victor.presenter.MeiPaiPresenter;import com.victor.presenter.OnMeiPaiListener;import com.victor.seagull.view.MeiPaiView;import java.util.List;/** * Created by victor on 2017/4/27. */public class MeiPaiPresenterImpl implements MeiPaiPresenter,OnMeiPaiListener{    /*Presenter作为中间层，持有View和Model的引用*/    private MeiPaiView meiPaiView;    private MeiPaiModel meiPaiModel;    public MeiPaiPresenterImpl (MeiPaiView meiPaiView) {        this.meiPaiView = meiPaiView;        meiPaiModel = new MeiPaiModelImpl();    }    @Override    public void getDatas(String url,int currentPage) {        meiPaiView.showLoading();        meiPaiModel.loadMeiPai(url,currentPage,this);    }    @Override    public void onSuccess(List<MeiPaiData> meiPaiDatas) {        meiPaiView.hideLoading();        meiPaiView.setDatas(meiPaiDatas);    }    @Override    public void onError(String error) {        meiPaiView.hideLoading();        meiPaiView.showError(error);    }}