package com.victor.seagull;import android.content.Intent;import android.graphics.Color;import android.os.Build;import android.os.Bundle;import android.view.View;import android.view.Window;import android.view.WindowManager;import com.victor.model.data.Theme;import com.victor.update.library.ui.UpdateActivity;import com.victor.util.AndroidBug54971Workaround;import com.victor.util.SharePreferencesUtil;import com.victor.util.StatusBarUtil;/** * Created by victor on 2016/2/2. */public class BaseActivity extends UpdateActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        initialize();    }    private void initialize () {        /*if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.KITKAT) {            WindowManager.LayoutParams localLayoutParams = getWindow().getAttributes();            localLayoutParams.flags = (WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS | localLayoutParams.flags);        }*/       /* if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.LOLLIPOP) {            Window window = getWindow();            window.clearFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS);            window.getDecorView().setSystemUiVisibility(View.SYSTEM_UI_FLAG_LAYOUT_HIDE_NAVIGATION);            window.addFlags(WindowManager.LayoutParams.FLAG_DRAWS_SYSTEM_BAR_BACKGROUNDS);            window.setStatusBarColor(Color.TRANSPARENT);        }*/        StatusBarUtil.translucentStatusBar(this,true,false,false);        AndroidBug54971Workaround.assistActivity(findViewById(android.R.id.content));        setTheme();    }    private void setTheme() {        Theme theme = SharePreferencesUtil.getCurrentTheme(this);        switch (theme) {            case Blue:                setTheme(R.style.BlueTheme);                break;            case Red:                setTheme(R.style.RedTheme);                break;            case Brown:                setTheme(R.style.BrownTheme);                break;            case Green:                setTheme(R.style.GreenTheme);                break;            case Purple:                setTheme(R.style.PurpleTheme);                break;            case Teal:                setTheme(R.style.TealTheme);                break;            case Pink:                setTheme(R.style.PinkTheme);                break;            case DeepPurple:                setTheme(R.style.DeepPurpleTheme);                break;            case Orange:                setTheme(R.style.OrangeTheme);                break;            case Indigo:                setTheme(R.style.IndigoTheme);                break;            case LightGreen:                setTheme(R.style.LightGreenTheme);                break;            case Lime:                setTheme(R.style.LimeTheme);                break;            case DeepOrange:                setTheme(R.style.DeepOrangeTheme);                break;            case Cyan:                setTheme(R.style.CyanTheme);                break;            case BlueGrey:                setTheme(R.style.BlueGreyTheme);                break;        }    }    protected String getName() {        return BaseActivity.class.getName();    }    @Override    public void onBackPressed() {        super.onBackPressed();        overridePendingTransition(R.anim.right_in_back, R.anim.right_out_back);    }    @Override    public void startActivity(Intent intent) {        super.startActivity(intent);        overridePendingTransition(R.anim.right_in, R.anim.right_out);    }    @Override    protected void onDestroy() {        super.onDestroy();    }}