package com.victor.seagull;import android.content.Intent;import android.os.Handler;import android.os.Message;import android.os.Bundle;import android.view.View;import android.view.animation.Animation;import android.view.animation.AnimationUtils;import android.widget.RelativeLayout;import com.victor.update.library.util.AppUtil;import com.victor.util.Constant;import com.victor.widget.ColorMovingTextView;public class WelcomeActivity extends BaseActivity {    private RelativeLayout mRlWecome;    private ColorMovingTextView mMtvVersion;    private View view;    Handler mHandler = new Handler() {        @Override        public void handleMessage(Message msg) {            switch (msg.what) {                case Constant.Msg.GOTO_WONDERFUL_LIVE:                    startActivity(new Intent(WelcomeActivity.this, MainActivity.class));                    finish();                    break;            }        }    };    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        view = getLayoutInflater().from(this).inflate(R.layout.activity_welcome,null);        setContentView(view);        mRlWecome = (RelativeLayout) findViewById(R.id.rl_welcome);        mMtvVersion = (ColorMovingTextView) findViewById(R.id.mtv_version);        mMtvVersion.setText("当前版本：v" + AppUtil.getAppVersionName(this));        Animation animation = AnimationUtils.loadAnimation(this, R.anim.scale);        mRlWecome.startAnimation(animation);        mHandler.sendEmptyMessageDelayed(Constant.Msg.GOTO_WONDERFUL_LIVE, 1000);    }}