package com.victor.util;import android.util.Log;/** * Create by Victor on 2016/5/16 * 日志打印工具 */public class Loger {	public static void d (String TAG, Object msg) {		if (Constant.isDebug) {			Log.d(TAG, msg.toString());		}	}	public static void e (String TAG, Object msg) {		if (Constant.isDebug) {			Log.e(TAG, msg.toString());		}	}	public static void i (String TAG, Object msg) {		if (Constant.isDebug) {			Log.i(TAG, msg.toString());		}	}}