package com.victor.util;import android.annotation.SuppressLint;import android.support.v4.view.WindowInsetsCompat;import android.view.Gravity;public class WindowInsetsCompatUtil {    public static WindowInsetsCompat copy(WindowInsetsCompat source) {        return copyExcluded(source, Gravity.NO_GRAVITY);    }    @SuppressLint("RtlHardcoded")    public static WindowInsetsCompat copyExcluded(WindowInsetsCompat source, int gravity) {        int l = (gravity & Gravity.LEFT) == Gravity.LEFT ? 0 : source.getSystemWindowInsetLeft();        int t = (gravity & Gravity.TOP) == Gravity.TOP ? 0 : source.getSystemWindowInsetTop();        int r = (gravity & Gravity.RIGHT) == Gravity.RIGHT ? 0 : source.getSystemWindowInsetRight();        int b = (gravity & Gravity.BOTTOM) == Gravity.BOTTOM ? 0 : source.getSystemWindowInsetBottom();        return source.replaceSystemWindowInsets(l, t, r, b);    }}