package com.victor.widget;import android.content.Context;import android.content.res.Resources;import android.support.design.widget.FloatingActionButton;import android.util.AttributeSet;import android.view.View;import com.victor.interfaces.ColorUiInterface;import com.victor.util.ViewAttributeUtil;/** * Created by victor on 2017/4/26. */public class ColorFloatingActionButton extends FloatingActionButton implements ColorUiInterface {    private int attr_background = -1;    public ColorFloatingActionButton(Context context) {        super(context);    }    public ColorFloatingActionButton(Context context, AttributeSet attrs) {        super(context, attrs);        this.attr_background = ViewAttributeUtil.getBackgroundAttibute(attrs);    }    public ColorFloatingActionButton(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);        this.attr_background = ViewAttributeUtil.getBackgroundAttibute(attrs);    }    @Override    public View getView() {        return this;    }    @Override    public void setTheme(Resources.Theme themeId) {        if (attr_background != -1) {            ViewAttributeUtil.applyBackgroundTintColor(this, themeId, attr_background);        }    }}