package com.victor.widget;import android.content.Context;import android.content.res.Resources;import android.graphics.Rect;import android.support.design.widget.AppBarLayout;import android.support.design.widget.CoordinatorLayout;import android.util.AttributeSet;import android.view.View;import com.victor.interfaces.ColorUiInterface;import com.victor.util.ViewAttributeUtil;@CoordinatorLayout.DefaultBehavior(InsetsAppBarLayout.Behavior.class)public class InsetsAppBarLayout extends AppBarLayout implements WindowInsetsHandler,ColorUiInterface {    private int attr_background = -1;    public InsetsAppBarLayout(Context context) {        super(context);    }    public InsetsAppBarLayout(Context context, AttributeSet attrs) {        super(context, attrs);        this.attr_background = ViewAttributeUtil.getBackgroundAttibute(attrs);    }    @Override    public boolean onApplyWindowInsets(Rect insets) {        return WindowInsetsHelper.dispatchApplyWindowInsets(this, insets);    }    @Override    public View getView() {        return this;    }    @Override    public void setTheme(Resources.Theme themeId) {        if (attr_background != -1) {            ViewAttributeUtil.applyBackgroundDrawable(this, themeId, attr_background);        }    }    public static class Behavior extends AppBarLayout.Behavior            implements InsetsCoordinatorLayout.WindowInsetsHandlingBehavior {        public Behavior() {        }        public Behavior(Context context, AttributeSet attrs) {            super(context, attrs);        }        @Override        public boolean onApplyWindowInsets(CoordinatorLayout layout, View child, Rect insets) {            return WindowInsetsHelper.onApplyWindowInsets(child, insets);        }    }}